import{_ as e,c as l,o as r,b as t}from"./app.062798f6.js";const f='{"title":"\u89E3\u6790\u6D41\u7A0B","description":"","frontmatter":{},"headers":[{"level":2,"title":"\u89E3\u6790\u6D41\u7A0B","slug":"\u89E3\u6790\u6D41\u7A0B"},{"level":2,"title":"DOMContentLoaded\u4E0Eload","slug":"domcontentloaded\u4E0Eload"},{"level":2,"title":"\u6E32\u67D3\u6D41\u7A0B","slug":"\u6E32\u67D3\u6D41\u7A0B"},{"level":2,"title":"\u5728\u6BCF\u4E00\u5E27\u4E2D\u5B8C\u6210\u7684\u5DE5\u4F5C","slug":"\u5728\u6BCF\u4E00\u5E27\u4E2D\u5B8C\u6210\u7684\u5DE5\u4F5C"},{"level":2,"title":"\u4F7F\u7528Chrome\u8C03\u8BD5\u5DE5\u5177\u67E5\u770B\u6E32\u67D3\u6D41\u7A0B","slug":"\u4F7F\u7528chrome\u8C03\u8BD5\u5DE5\u5177\u67E5\u770B\u6E32\u67D3\u6D41\u7A0B"}],"relativePath":"\u524D\u7AEF/HTML/\u6D4F\u89C8\u5668\u89E3\u6790\u4E0E\u6E32\u67D3\u6D41\u7A0B.md"}',a={},o=t('<h2 id="\u89E3\u6790\u6D41\u7A0B" tabindex="-1">\u89E3\u6790\u6D41\u7A0B <a class="header-anchor" href="#\u89E3\u6790\u6D41\u7A0B" aria-hidden="true">#</a></h2><p>\u53C2\u8003</p><ul><li><a href="http://www.shymean.com/article/%E6%B5%8F%E8%A7%88%E5%99%A8%E8%A7%A3%E6%9E%90HTML%E7%9A%84%E6%B5%81%E7%A8%8B" target="_blank" rel="noopener noreferrer">\u6D4F\u89C8\u5668\u89E3\u6790HTML\u7684\u6D41\u7A0B</a></li></ul><p>\u5F53\u6D4F\u89C8\u5668<strong>\u4ECE\u4E0A\u5230\u4E0B</strong>\u89E3\u6790\u6574\u4E2AHTML\u6587\u6863\u65F6\uFF0C</p><p>\u5982\u679C\u9047\u89C1\u5916\u90E8URL\u8D44\u6E90\uFF0C\u5C31\u4F1A\u53D1\u9001\u8BF7\u6C42\u52A0\u8F7D\u5BF9\u5E94\u6587\u4EF6\uFF08\u73B0\u4EE3\u6D4F\u89C8\u5668\u53EF\u80FD\u4F1A\u540C\u65F6\u53D1\u9001\u591A\u4E2A\u8BF7\u6C42\u52A0\u8F7D\u6587\u4EF6\uFF09\u3002</p><p>\u5982\u679C\u9047\u89C1\u5185\u8054\u7684\u6837\u5F0F\u8868\uFF0C\u5C31\u4F1A\u7ACB\u5373\u89E3\u6790\uFF08\u4F46\u4E0D\u4E00\u5B9A\u4F1A\u7ACB\u5373\u6E32\u67D3\u51FA\u6837\u5F0F\uFF09\uFF1B\u5982\u679C\u9047\u89C1\u5185\u8054\u7684\u811A\u672C\uFF0C\u5C31\u4F1A\u7ACB\u5373\u89E3\u6790\u548C\u6267\u884C\uFF1B</p><p><strong>\u6837\u5F0F\u8868\u963B\u585E</strong></p><p>\u5F53HTML\u89E3\u6790\u5668\u9047\u89C1\u4E00\u4E2Astyle\u6807\u7B7E\u65F6\uFF0C\u4F1A\u6309\u987A\u5E8F\u89E3\u6790\u91CC\u9762\u7684\u6837\u5F0F\uFF1B\u5F53HTML\u89E3\u6790\u5668\u9047\u89C1\u4E00\u4E2Alink\u6807\u7B7E\uFF0C\u4F1A\u53D1\u9001\u4E00\u4E2A\u5916\u90E8\u6837\u5F0F\u8868\u7684\u8BF7\u6C42\u3002\u8FD9\u6837\u7684\u540E\u679C\u662F\u5230\u5BFC\u81F4\u540E\u9762\u7684\u540E\u9762\u7684JS\u4EE3\u7801</p><ul><li>\u5982\u679C\u662F\u5185\u8054\u811A\u672C\uFF0C\u5219\u5FC5\u987B\u7B49\u5F85\u524D\u9762\u7684\u6837\u5F0F\u8868\u52A0\u8F7D\u548C\u89E3\u6790\u5B8C\u6210\u624D\u4F1A\u6267\u884C</li><li>\u5982\u679C\u662F\u5916\u90E8\u811A\u672C\uFF0C\u6D4F\u89C8\u5668\u4F1A\u53D1\u9001\u4E0B\u8F7D\u5916\u90E8\u811A\u672C\u6587\u4EF6\u7684\u8BF7\u6C42\uFF08CSS\u6587\u4EF6\u548CJS\u6587\u4EF6\u53EF\u80FD\u540C\u6B65\u4E0B\u8F7D\uFF09\uFF0C\u5373\u4F7F\u6587\u4EF6\u5DF2\u7ECF\u8FD4\u56DE\uFF0C\u4E5F\u5FC5\u987B\u7B49\u5F85\u524D\u9762\u7684\u6837\u5F0F\u8868\u52A0\u8F7D\u548C\u89E3\u6790\u5B8C\u6210</li></ul><p>\u8FD9\u662F\u56E0\u4E3AJS\u6267\u884C\u4F9D\u8D56\u6700\u65B0\u7684CSS\u6E32\u67D3\uFF08\u6216\u8005\u8BF4\u6700\u7CBE\u786E\u7684\u6837\u5F0F\u4FE1\u606F\uFF09\u3002</p><p><strong>\u811A\u672C\u963B\u585E</strong></p><p>\u5982\u679C\u9047\u89C1\u666E\u901A\u7684script\uFF08\u65E0async\u6216defer\uFF09\u65F6\uFF0C\u4E0D\u8BBA\u4ED6\u662F\u5185\u8054\u811A\u672C\u8FD8\u662F\u5916\u90E8\u811A\u672C\uFF0C\u90FD\u4F1A\u963B\u585E\u6D4F\u89C8\u5668\u8FDB\u4E00\u6B65\u89E3\u6790HTML\u6587\u6863\uFF08\u5373\u6682\u65F6\u65E0\u6CD5\u5904\u7406\u8FD9\u4E2A\u811A\u672C\u540E\u9762\u5176\u4ED6\u9700\u8981\u52A0\u8F7D\u7684URL\uFF09\uFF0C\u800C\u5FC5\u987B\u7B49\u5F85\u8BE5\u6807\u7B7E\u4EE3\u8868\u7684\u811A\u672C\u6587\u4EF6\u6267\u884C\u5B8C\u6BD5\uFF08\u5982\u679C\u662F\u5916\u8054\u7684\u811A\u672C\uFF0C\u751A\u81F3\u9700\u8981\u7B49\u5230\u8FD9\u4E2Ajs\u6587\u4EF6\u52A0\u8F7D\u6210\u529F\u5E76\u6267\u884C\u5B8C\u6BD5\u3002</p><p>\u8FD9\u662F\u56E0\u4E3A\uFF1AJS\u53EF\u80FD\u5F71\u54CD\u540E\u7EED\u7684\u6587\u6863\uFF0C\u53EF\u80FD\u5411\u6587\u6863\u6D41\u4E2D\u63D2\u5165\u4FE1\u606F\uFF0C\u4E5F\u53EF\u80FD\u6539\u53D8\u540E\u7EEDscript\u811A\u672C\u7684\u5168\u5C40\u53D8\u91CF\uFF0C\u56E0\u6B64\u6D4F\u89C8\u5668\u5E72\u8106\u5728\u89E3\u6790\u548C\u6267\u884Cscript\u6807\u7B7E\u7684\u65F6\u5019\uFF0C\u963B\u585E\u540E\u7EED\u6587\u6863\u7684\u89E3\u6790</p><p>\u6574\u7406\u4E0A\u9762\u53EF\u4EE5\u5F97\u5230\u4E0B\u9762\u7ED3\u8BBA</p><ul><li>CSS\u6587\u6863\u7684\u52A0\u8F7D\u548C\u89E3\u6790\uFF0C\u963B\u585E\u7684\u662F\u811A\u672C\u7684\u6267\u884C\u800C\u4E0D\u662F\u811A\u672C\u7684\u52A0\u8F7D\u3002</li><li>\u540C\u6B65JS\u811A\u672C\u7684\u52A0\u8F7D\u89E3\u6790\u548C\u6267\u884C\uFF0C\u662F\u4F1A\u5F71\u54CDHTML\u89E3\u6790\u5668\u7684\u5DE5\u4F5C\uFF0C\u5BFC\u81F4\u540E\u9762\u7684\u6240\u6709\u8D44\u6E90\u90FD\u65E0\u6CD5\u88AB\u52A0\u8F7D\u3002</li></ul><h2 id="domcontentloaded\u4E0Eload" tabindex="-1">DOMContentLoaded\u4E0Eload <a class="header-anchor" href="#domcontentloaded\u4E0Eload" aria-hidden="true">#</a></h2><p>\u5F53\u6587\u6863\u4E2D\u6CA1\u6709\u811A\u672C\u65F6\uFF0C\u6D4F\u89C8\u5668\u89E3\u6790\u5B8C\u6587\u6863\u4FBF\u80FD\u89E6\u53D1 DOMContentLoaded \u4E8B\u4EF6\uFF1B\u5982\u679C\u6587\u6863\u4E2D\u5305\u542B\u811A\u672C\uFF0C\u5219\u811A\u672C\u4F1A\u963B\u585E\u6587\u6863\u7684\u89E3\u6790\uFF0C\u800C\u811A\u672C\u9700\u8981\u7B49\u4F4D\u4E8E\u811A\u672C\u524D\u9762\u7684css\u52A0\u8F7D\u5B8C\u624D\u80FD\u6267\u884C\u3002\u5728\u4EFB\u4F55\u60C5\u51B5\u4E0B\uFF0CDOMContentLoaded \u7684\u89E6\u53D1\u4E0D\u9700\u8981\u7B49\u5F85\u56FE\u7247\u7B49\u5176\u4ED6\u8D44\u6E90\u52A0\u8F7D\u5B8C\u6210\u3002</p><p>\u9875\u9762\u4E0A\u6240\u6709\u7684\u8D44\u6E90\uFF08\u56FE\u7247\uFF0C\u97F3\u9891\uFF0C\u89C6\u9891\u7B49\uFF09\u88AB\u52A0\u8F7D\u4EE5\u540E\u624D\u4F1A\u89E6\u53D1load\u4E8B\u4EF6\uFF0C\u7B80\u5355\u6765\u8BF4\uFF0C\u9875\u9762\u7684load\u4E8B\u4EF6\u4F1A\u5728DOMContentLoaded\u88AB\u89E6\u53D1\u4E4B\u540E\u624D\u89E6\u53D1\u3002</p><p>\u53C2\u8003</p><ul><li><a href="https://developer.mozilla.org/zh-CN/docs/Web/Events/DOMContentLoaded" target="_blank" rel="noopener noreferrer">DOMContentLoaded</a></li><li><a href="https://www.cnblogs.com/caizhenbo/p/6679478.html" target="_blank" rel="noopener noreferrer">DOMContentLoaded\u4E0Eload\u7684\u533A\u522B</a></li></ul><p><code>script</code>\u6807\u7B7E\u4E0A\u7684<code>async</code>\u548C<code>defer</code>\u7684\u533A\u522B\uFF0C\u53C2\u8003<a href="https://developer.mozilla.org/zh-CN/docs/Web/HTML/Element/script" target="_blank" rel="noopener noreferrer">Script - MDN</a></p><p>script\u6807\u7B7E\u4E0A\u7684<code>async</code>\u548C<code>defer</code>\u5C5E\u6027\uFF0C\u51B3\u5B9A\u4E86\u811A\u672C\u52A0\u8F7D\u65F6\u91C7\u7528\u540C\u6B65\u65B9\u5F0F\u8FD8\u662F\u5F02\u6B65\u65B9\u5F0F\u3002</p><ul><li>\u5982\u679C\u4E0D\u52A0\u4E0A\u8FD9\u4E24\u4E2A\u5C5E\u6027\uFF0C\u9ED8\u8BA4\u4E3A\u540C\u6B65\u52A0\u8F7D\u811A\u672C\uFF0C\u52A0\u8F7D\u548C\u6267\u884C\u65F6\u4F1A\u963B\u585E\u9875\u9762\u7684\u6E32\u67D3\uFF0C\u5373\u6D4F\u89C8\u5668\u6309\u987A\u5E8F\u89E3\u6790DOM\u6811\u53CA\u811A\u672C\uFF0C\u9047\u89C1\u811A\u672C\u4F1A\u963B\u585EDOM\u6811\u751F\u6210\u5E76\u6267\u884C\u811A\u672C\u3002</li><li>async \u548C defer \u65B9\u5F0F\u662F\u7528\u5F02\u6B65\u7684\u65B9\u5F0F\u52A0\u8F7D\u811A\u672C\uFF0C\u4E0D\u4F1A\u963B\u585E\u9875\u9762\u6E32\u67D3\uFF0C\u5B83\u4EEC\u4E4B\u95F4\u7684\u4E0D\u540C\u5728\u4E8E\u4F55\u65F6\u6267\uFF0C <ul><li>async \u65B9\u5F0F\u662F\u52A0\u8F7D\u540E\u9A6C\u4E0A\u6267\u884C\uFF0C</li><li>defer \u65B9\u5F0F\u662F\u52A0\u8F7D\u540E\u7B49\u6240\u6709 DOM \u90FD\u6E32\u67D3\u597D\u89E6\u53D1 DOMContentLoaded \u4E8B\u4EF6\u4E4B\u524D\u6267\u884C\uFF0C</li><li>\u6240\u4EE5 async \u65B9\u5F0F\u91CC\u9762\u7684\u811A\u672C\u90FD\u662F\u4E71\u5E8F\u6267\u884C\uFF0Cdefer \u65B9\u5F0F\u52A0\u8F7D\u7684\u4EE3\u7801\u90FD\u662F\u6309\u5E8F\u6267\u884C\u7684\uFF0C\u6309\u5E8F\u6267\u884C\u5BF9\u6709\u4F9D\u8D56\u7684\u4EE3\u7801\u975E\u5E38\u91CD\u8981\u3002</li><li>\u82E5\u4E24\u4E2A\u5C5E\u6027\u540C\u5728\uFF0C\u4F1A\u5FFD\u7565defer\u800C\u9075\u4ECEasync</li></ul></li></ul><p>\u901A\u8FC7\u52A8\u6001\u751F\u6210script\u7684\u65B9\u6CD5\uFF0C\u53EF\u4EE5\u5B9E\u73B0<code>JSONP</code>\u7B49\u529F\u80FD</p><h2 id="\u6E32\u67D3\u6D41\u7A0B" tabindex="-1">\u6E32\u67D3\u6D41\u7A0B <a class="header-anchor" href="#\u6E32\u67D3\u6D41\u7A0B" aria-hidden="true">#</a></h2><p>\u53C2\u8003\uFF1A</p><ul><li><a href="https://coolshell.cn/articles/9666.html" target="_blank" rel="noopener noreferrer">\u6D4F\u89C8\u5668\u7684\u6E32\u67D3\u539F\u7406\u7B80\u4ECB</a></li><li><a href="http://www.shymean.com/article/%E6%B5%8F%E8%A7%88%E5%99%A8%E6%B8%B2%E6%9F%93%E6%B5%81%E7%A8%8B" target="_blank" rel="noopener noreferrer">\u6D4F\u89C8\u5668\u6E32\u67D3\u6D41\u7A0B</a></li></ul><p>\u4E0B\u9762\u5C55\u793A\u4E86\u5927\u6982\u7684\u6E32\u67D3\u8FC7\u7A0B</p><p><img src="http://img.shymean.com/oPic/1620309416803_942.png" alt=""></p><ul><li>\u6D4F\u89C8\u5668\u9996\u5148\u89E3\u6790\u4E09\u4E2A\u4E1C\u897F\uFF1A <ul><li>HTML/SVG/XHTML\uFF0C\u4EA7\u751F\u4E00\u4E2ADOM Tree\u3002</li><li>CSS\uFF0C\u4EA7\u751FCSS Rule Tree\u3002</li><li>Javascript\uFF0C\u4E3B\u8981\u662F\u901A\u8FC7DOM API\u548CCSSOM API\u6765\u64CD\u4F5CDOM Tree\u548CCSS Rule Tree.</li></ul></li><li>\u89E3\u6790\u5B8C\u6210\u540E\uFF0C\u6D4F\u89C8\u5668\u5F15\u64CE\u4F1A\u901A\u8FC7DOM Tree \u548C CSS Rule Tree \u6765\u6784\u9020 Rendering Tree\u3002\u6CE8\u610F\uFF1A <ul><li>Rendering Tree \u6E32\u67D3\u6811\u5E76\u4E0D\u7B49\u540C\u4E8EDOM\u6811\uFF0C\u56E0\u4E3A\u4E00\u4E9B\u50CFHead\u6216display:none\u7684\u4E1C\u897F\u5C31\u6CA1\u5FC5\u8981\u653E\u5728\u6E32\u67D3\u6811\u4E2D\u4E86\u3002</li><li>CSS \u7684 Rule Tree\u4E3B\u8981\u662F\u4E3A\u4E86\u5B8C\u6210\u5339\u914D\u5E76\u628ACSS Rule\u9644\u52A0\u4E0ARendering Tree\u4E0A\u7684\u6BCF\u4E2ADOM\u7ED3\u70B9\u3002</li><li>\u7136\u540E\u8BA1\u7B97DOM\u7ED3\u70B9\u7684\u4F4D\u7F6E\uFF0C\u8FD9\u53C8\u53EBlayout\u548Creflow\u8FC7\u7A0B\u3002</li></ul></li><li>\u6700\u540E\u901A\u8FC7\u8C03\u7528\u64CD\u4F5C\u7CFB\u7EDFNative GUI\u7684API\u7ED8\u5236\u3002</li></ul><p><strong>Repaint\u91CD\u7ED8</strong></p><p>Repaint\u8868\u793A\u9875\u9762\u4E0A\u67D0\u4E2A\u5143\u7D20\u7684\u7684\u975E\u5B9A\u4F4D\u6837\u5F0F\u9700\u8981\u91CD\u65B0\u7ED8\u5236\uFF08\u6BD4\u5982\u5229\u7528JS\u6539\u53D8\u4E86\u5143\u7D20\u8282\u70B9\u7684\u80CC\u666F\u989C\u8272\uFF0C\u6B64\u65F6\u4E0D\u9700\u8981\u91CD\u65B0\u5E03\u5C40\uFF09</p><p><strong>Reflow\u56DE\u6D41</strong></p><p>Reflow\u8868\u793A\u5143\u7D20\u8282\u70B9\u7684\u51E0\u4F55\u5C3A\u5BF8\u53D1\u751F\u4E86\u53D8\u5316\uFF0C\u6B64\u65F6\u9700\u8981\u91CD\u65B0\u8BA1\u7B97\u5E76\u6784\u5EFA\u6E32\u67D3\u6811\uFF08\u5373\u6B64\u65F6\u9700\u8981\u91CD\u65B0\u5E03\u5C40\uFF09\uFF0C\u6210\u672C\u6BD4Repaint\u7684\u6210\u672C\u9AD8\u5F97\u591A\u7684\u591A</p><p>\u4E0B\u9762\u662F\u51E0\u6761\u51CF\u5C11Repaint\u548CReflow\u7684\u539F\u5219</p><ul><li>\u4E0D\u8981\u4E00\u6761\u4E00\u6761\u5730\u4FEE\u6539DOM\u7684\u6837\u5F0F\uFF0C\u5982\u679C\u4FEE\u6539\u7684\u6837\u5F0F\u8FC7\u591A\u53EF\u4EE5\u5C06\u6837\u5F0F\u7EDF\u4E00\u5728\u67D0\u4E2A\u7C7B\u4E2D\uFF0C\u7136\u540E\u76F4\u63A5\u66F4\u6539\u5143\u7D20\u8282\u70B9\u7684className\uFF1B</li><li>\u4F7F\u7528\u4E34\u65F6\u53D8\u91CF\u4FDD\u5B58DOM\u8282\u70B9\uFF0C\u800C\u4E0D\u662F\u6BCF\u6B21\u90FD\u76F4\u63A5\u5BF9DOM\u8282\u70B9\u8FDB\u884C\u64CD\u4F5C\uFF08\u51CF\u5C11\u5143\u7D20\u8282\u70B9\u7684\u8BFB\u5199\uFF09\uFF0C\u5728JS\u6027\u80FD\u4E0E\u6D4F\u89C8\u5668\u6027\u80FD\u65B9\u9762\u90FD\u80FD\u5F97\u5230\u4E00\u4E9B\u4F18\u5316\uFF1B</li><li>\u5C3D\u53EF\u80FD\u4FEE\u6539\u5C42\u7EA7\u6BD4\u8F83\u4F4E\u7684DOM\uFF0C\u7F29\u5C0F\u64CD\u4F5C\u7684\u5F71\u54CD\u8303\u56F4\uFF1B</li><li>\u653E\u5F03\u4F7F\u7528table\u8FDB\u884C\u5E03\u5C40\uFF0C\u56E0\u4E3A\u4E00\u4E2A\u5F88\u5C0F\u7684\u6539\u52A8\u90FD\u4F1A\u9020\u6210\u6574\u4E2Atable\u7684\u91CD\u65B0\u5E03\u5C40</li></ul><h2 id="\u5728\u6BCF\u4E00\u5E27\u4E2D\u5B8C\u6210\u7684\u5DE5\u4F5C" tabindex="-1">\u5728\u6BCF\u4E00\u5E27\u4E2D\u5B8C\u6210\u7684\u5DE5\u4F5C <a class="header-anchor" href="#\u5728\u6BCF\u4E00\u5E27\u4E2D\u5B8C\u6210\u7684\u5DE5\u4F5C" aria-hidden="true">#</a></h2><p>\u53C2\u8003\uFF1A<a href="https://mp.weixin.qq.com/s/CxD7SKzyRMS0rMY6jDlVvg" target="_blank" rel="noopener noreferrer">\u770B\u4E86\u5C31\u4F1A\u7684\u6D4F\u89C8\u5668\u5E27\u539F\u7406</a></p><p><img src="http://img.shymean.com/oPic/1620309535700_941.png" alt=""></p><p>\u53EF\u4EE5\u770B\u89C1<code>requestAnimationFrame</code>\u662F\u5728\u5E27\u5F00\u59CB\u7684\u65F6\u673A\u6267\u884C\u7684\uFF0C\u800C<code>requestIdleCallback</code>\u662F\u5728\u5E27\u672B\u5C3E\u7684\u7A7A\u95F2\u65F6\u95F4\u6267\u884C\u7684</p><h2 id="\u4F7F\u7528chrome\u8C03\u8BD5\u5DE5\u5177\u67E5\u770B\u6E32\u67D3\u6D41\u7A0B" tabindex="-1">\u4F7F\u7528Chrome\u8C03\u8BD5\u5DE5\u5177\u67E5\u770B\u6E32\u67D3\u6D41\u7A0B <a class="header-anchor" href="#\u4F7F\u7528chrome\u8C03\u8BD5\u5DE5\u5177\u67E5\u770B\u6E32\u67D3\u6D41\u7A0B" aria-hidden="true">#</a></h2><p>\u53C2\u8003</p><ul><li><a href="https://blog.csdn.net/u010582082/article/details/70761524" target="_blank" rel="noopener noreferrer">\u901A\u8FC7chrome\u8C03\u8BD5\u5668\u6D4B\u8BD5\u4E86\u89E3\u6D4F\u89C8\u5668\u89E3\u6790\u548C\u6E32\u67D3HTML\u7684\u8FC7\u7A0B</a></li></ul>',43),n=[o];function i(d,p,c,s,h,u){return r(),l("div",null,n)}var M=e(a,[["render",i]]);export{f as __pageData,M as default};
