import{_ as a,c as p,o as s,a2 as n}from"./chunks/framework.DLPB9-dE.js";const g=JSON.parse('{"title":"PHP","description":"","frontmatter":{},"headers":[],"relativePath":"后端/PHP.md","filePath":"后端/PHP.md","lastUpdated":1683863238000}'),e={name:"后端/PHP.md"},t=n(`<h1 id="php" tabindex="-1">PHP <a class="header-anchor" href="#php" aria-label="Permalink to &quot;PHP&quot;">​</a></h1><blockquote><p>php是世界上最好的语言</p></blockquote><h2 id="包管理工具composer" tabindex="-1">包管理工具composer <a class="header-anchor" href="#包管理工具composer" aria-label="Permalink to &quot;包管理工具composer&quot;">​</a></h2><p><a href="https://getcomposer.org/" target="_blank" rel="noreferrer">Composer</a>是PHP的软件包管理系统，它提供用于管理PHP软件和依赖库关系的标准格式</p><h2 id="nginx代理php服务器" tabindex="-1">Nginx代理php服务器 <a class="header-anchor" href="#nginx代理php服务器" aria-label="Permalink to &quot;Nginx代理php服务器&quot;">​</a></h2><p>除了使用wamp、mamp等集成工具搭建php开发环境之外，还使用nginx 搭建php运行环境</p><p>参考</p><ul><li><a href="https://segmentfault.com/a/1190000014610688" target="_blank" rel="noreferrer">Nginx和PHP的配置</a></li><li><a href="https://blog.csdn.net/ivan820819/article/details/54970330" target="_blank" rel="noreferrer">php-fpm安装、配置与优化</a></li></ul><p>PHP在 5.3.3 之后已经将<code>php-fpm</code>写入php源码核心，在安装php的时候，通过<code>–enable-fpm</code>自动进行编译。其实质是一个 PHP FastCGI管理器，会启动一个本地服务器</p><h3 id="启动php-fpm" tabindex="-1">启动php-fpm <a class="header-anchor" href="#启动php-fpm" aria-label="Permalink to &quot;启动php-fpm&quot;">​</a></h3><p>首先启动php-fpm</p><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code"><code><span class="line"><span>sudo php-fpm</span></span></code></pre></div><p>php-fpm的默认启动端口号为9000,可以在<code>/etc/php-fpm.conf</code>中修改</p><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code"><code><span class="line"><span>listen = 127.0.0.1:9000</span></span></code></pre></div><h3 id="修改nginx配置" tabindex="-1">修改nginx配置 <a class="header-anchor" href="#修改nginx配置" aria-label="Permalink to &quot;修改nginx配置&quot;">​</a></h3><p>接下来修改nginx的配置</p><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code"><code><span class="line"><span>server {</span></span>
<span class="line"><span>    listen 80;</span></span>
<span class="line"><span>    server_name phptest.com;</span></span>
<span class="line"><span>    </span></span>
<span class="line"><span>    root         /Users/Txm/test; # php 项目根目录</span></span>
<span class="line"><span>    index index.php index.html index.htm;</span></span>
<span class="line"><span></span></span>
<span class="line"><span>    location ~ \\.php$ {</span></span>
<span class="line"><span>        # 设置监听php-fpm的端口</span></span>
<span class="line"><span>        fastcgi_pass   127.0.0.1:9000;</span></span>
<span class="line"><span>        fastcgi_index  index.php;</span></span>
<span class="line"><span>        fastcgi_param  SCRIPT_FILENAME  $document_root$fastcgi_script_name;</span></span>
<span class="line"><span>        include        fastcgi_params;</span></span>
<span class="line"><span>    }</span></span>
<span class="line"><span>}</span></span></code></pre></div><h3 id="附录-php-fpm相关目录" tabindex="-1">附录:php-fpm相关目录 <a class="header-anchor" href="#附录-php-fpm相关目录" aria-label="Permalink to &quot;附录:php-fpm相关目录&quot;">​</a></h3><p>linux下</p><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code"><code><span class="line"><span>/usr/local/php/php</span></span>
<span class="line"><span>/usr/local/php/etc/php.ini</span></span>
<span class="line"><span>/usr/local/php/sbin/php-fpm</span></span>
<span class="line"><span>/usr/local/php/etc/php-fpm.conf</span></span></code></pre></div><p>mac下</p><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code"><code><span class="line"><span>/usr/bin/php</span></span>
<span class="line"><span>/etc/php.ini</span></span>
<span class="line"><span>/usr/bin/php-fpm</span></span>
<span class="line"><span>/etc/php-fpm.conf</span></span></code></pre></div><h2 id="框架" tabindex="-1">框架 <a class="header-anchor" href="#框架" aria-label="Permalink to &quot;框架&quot;">​</a></h2><h3 id="thinkphp" tabindex="-1">ThinkPHP <a class="header-anchor" href="#thinkphp" aria-label="Permalink to &quot;ThinkPHP&quot;">​</a></h3><p>怎么说呢~</p><h3 id="larvel" tabindex="-1">Larvel <a class="header-anchor" href="#larvel" aria-label="Permalink to &quot;Larvel&quot;">​</a></h3><p>PHP中最现代化的框架之一</p>`,27),i=[t];function l(h,o,c,r,d,m){return s(),p("div",null,i)}const f=a(e,[["render",l]]);export{g as __pageData,f as default};
